#!/usr/bin/env bash
set -Eeuo pipefail

DIR="$( cd "$( dirname "${BASH_SOURCE[0]}" )" && pwd )"
cd "$DIR"

{
  echo; echo "Exceptions should not allow new packets to be parsed without starting over"

  {
    echo 8004000311048000002c800006c18000000080000000
    # Wrong length: should be 0x97
    echo 
  } | ./apdu.sh

  echo "After exception"

  {
    # Valid second packet, but no first packet.
    echo 
  } | ./apdu.sh || echo "Pass"
}

{
  echo; echo "Prefixed packets should cause Sign Unverified (ACCEPT THIS)"

  {
    echo 8004000311048000002c800006c18000000080000000
    echo 800401000103
    echo 
  } | ./apdu.sh
}

{
  echo; echo "Trailing packets should cause Sign Unverified (ACCEPT THIS)"

  {
    echo 8004000311048000002c800006c18000000080000000
    echo 
    echo 800481000103
  } | ./apdu.sh
}

{
  echo; echo "Prefixed and trailing packets should cause Sign Unverified (ACCEPT THIS)"

  {
    echo 8004000311048000002c800006c18000000080000000
    echo 800401000103
    echo 
    echo 800481000103
  } | ./apdu.sh
}

{
  echo; echo "Two valid packets should still cause Sign Unverified (ACCEPT THIS)"

  {
    echo 8004000311048000002c800006c18000000080000000
    echo 
    echo 
  } | ./apdu.sh
}

{
  echo; echo "Valid transaction should be parsed (ACCEPT THIS)"

  {
    echo 8004000311048000002c800006c18000000080000000
    echo 
  } | ./apdu.sh
}
